import React , {useState, useEffect} from "react";
import axios from "axios";

const PokeApi = () =>{

    const [num, setNum] = useState();
    const [name, setName] = useState();
    const [moves, setMoves] = useState();

    useEffect(()=>{
        async function getData(){
            const res = await axios.get(`https://pokeapi.co/api/v2/pokemon/${num}`);
            console.log(res.data.name);
            setName(res.data.name);
            setMoves(res.data.moves.length);
        }

        getData();
    });

    return(
        <>
        <h1 className="text-6xl font-bold text-center">You choose <span style={{color:'red'}}> {num} </span>value</h1>
        <h1 className="text-6xl font-bold text-center">Character is <span style={{color:'red'}}>{name}</span>  </h1>
        <h1 className="text-6xl font-bold text-center">  <span style={{color:'red'}}>{moves}</span> Moves</h1>
            <select value={num} onChange={(event)=>{
                setNum(event.target.value);
            }} 
            className="rounded-r-2xl bg-orange-500 font-bold text-4xl text-white">
                <option value="1">1</option>
                <option value="25">25</option>
                <option value="3">3</option>
                <option value="4">4</option>
                <option value="5">5</option>
            </select>
            <input></input>
        </>
    )
}

export default PokeApi;
